mapper.select:
SELECT
,u.username, u.nickname, u.email, u.phone, u.surname, u.`name`, u.createTime, u.state, u.cert FROM `user` u INNER JOIN user_detail ud ON u.id = ud.id WHERE u.id IN
#{id}

mapper.update:
update user_detail
SET contribution = contribution + #{contribution}
where id = #{id}

update user_detail
SET contribution = contribution - #{contribution}
where id = #{id} AND contribution > #{contribution}
